import {
    FETCH_PHONES_START,
    FETCH_PHONES_SUCCESS,
    FETCH_PHONES_FAILURE,
    FETCH_PHONES_BY_ID_START,
    FETCH_PHONES_BY_ID_SUCCESS,
    FETCH_PHONES_BY_ID_FAILURE,
    FETCH_TOPAY_START,
    FETCH_TOPAY_SUCCESS,
    FETCH_TOPAY_FAILURE
} from 'actionTypes';

import {
    fetchPhones as fetchPhonesApi,
    fetchPhonesById as fetchPhonesByIdApi
} from '../api';


export const fetchPhones = () => async dispatch => {
    dispatch({ type: FETCH_PHONES_START });
    try {
        const phones = await fetchPhonesApi();
        dispatch({
            type: FETCH_PHONES_SUCCESS,
            payload: phones
        })
    } catch (err) {
        dispatch({
            type: FETCH_PHONES_FAILURE,
            payload: err,
            error: true
        })
    }
};

export const fetchPhonesById = (id) => async dispatch => {
    dispatch({ type: FETCH_PHONES_BY_ID_START });
    try {
        const phoneById = await fetchPhonesByIdApi(id);
        dispatch({
            type: FETCH_PHONES_BY_ID_SUCCESS,
            payload: phoneById
        })
    } catch (err) {
        dispatch({
            type: FETCH_PHONES_BY_ID_FAILURE,
            payload: err,
            error: true
        })
    }
};

export const fetchToPay = (price) => async dispatch => {
    dispatch({ type: FETCH_TOPAY_START });
    try {
        dispatch({
            type: FETCH_TOPAY_SUCCESS,
            payload: price
        })
    } catch (err) {
        dispatch({
            type: FETCH_TOPAY_FAILURE,
            payload: err,
            error: true
        })
    }
};