import phones from './mockPhones';
const R = require('ramda');

export const fetchPhones = async () => {
    return new Promise(resolve => {
        resolve(phones);
    })
};

export const fetchPhonesById = async (id) => {
    return new Promise(resolve => {
        const phoneById = R.find(R.propEq('id', id), phones);
        resolve(phoneById);
    })
};