import React from 'react';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { Link } from 'react-router-dom';
import ShoppingCart from '@material-ui/icons/ShoppingCart';

const ItemCard = (props) => {
    const { phone } = props;
    return(
        <div>
            <Card style={{maxWidth: "250px", margin: "10px 0 0 10px", float: "left"}}>
                <CardMedia
                    style={{paddingTop: '56.25%', backgroundSize: "contain", margin: "10px"}}
                    image={phone.image}
                    title={phone.name}
                />
                <CardContent>
                    <Typography style={{margin: "5px 0 5px 0"}}>
                        <Link to={'/'}>
                            <div style={{float: "left", width: "85%"}}>{phone.name}</div>
                            <div style={{float: "left", width: "15%"}}>${phone.price}</div>
                        </Link>
                    </Typography>
                    <Typography>
                        {phone.description}
                    </Typography>
                </CardContent>
                <CardActions>
                    <Button variant="fab" mini  color="primary" aria-label="add">
                        <ShoppingCart />
                    </Button>
                    <Button size={'large'} variant="extendedFab" mini color="default" style={{fontSize: '11px'}}>
                        More Info
                    </Button>
                </CardActions>
            </Card>
        </div>
    );
};

export default ItemCard;