import React from 'react';
import { Card, CardActions, CardContent, CardMedia, Button, Typography } from '@material-ui/core';
import { Link } from 'react-router-dom';
import ShoppingCart from '@material-ui/icons/ShoppingCart';

const ItemCard = (props) => {
    const { phone } = props;
    return(
        <Card style={{maxWidth: "250px", margin: "0 10px 10px 0", float: "left"}}>
            <CardMedia
                style={{paddingTop: '56.25%', backgroundSize: "contain", margin: "10px"}}
                image={phone.image}
                title={phone.name}
            />
            <CardContent>
                <Typography style={{margin: "5px 0 5px 0"}}>
                    <Link to={'/'}>
                        <p style={{float: "left", width: "85%"}}>{phone.name}</p>
                        <p style={{float: "left", width: "15%"}}>${phone.price}</p>
                    </Link>
                </Typography>
                <Typography>
                    {phone.description}
                </Typography>
            </CardContent>
            <CardActions>
                <Button variant="fab" mini  color="primary" aria-label="add">
                    <ShoppingCart />
                </Button>
                <Button size={'large'}
                        variant="extendedFab"
                        mini
                        color="default"
                        style={{fontSize: '11px'}}>
                    More Info
                </Button>
            </CardActions>
        </Card>
    );
};

export default ItemCard;