import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { fetchPhones } from 'actions'
import { getPhones } from 'selectors';
import { Button } from '@material-ui/core';

import ItemCard from '../ItemCard';
import MainMenu from '../MainMenu';

class Main extends Component {
  componentWillMount() {
      this.props.fetchPhones();
  }

  render() {
    const phones = getPhones(this.props.storeState);
    const listCard =
    phones ? phones.map((phone, index) => {
        return (
            <div key={index}>
                <ItemCard phone={phone}/>
            </div>
        );
    }) : '';

    return (
      <div style={{padding: "10px"}}>
          <div style={{float: "left", width: "25%", marginRight: "3%"}}>
              <MainMenu/>
          </div>
          <div style={{float: "left", width: "72%", alignContent: "center"}}>
              {listCard}
              <Button
                  style={{fontSize: "13px", boxSizing: "content", margin: "5px 70% 10px 0"}}
                  variant={'contained'}
                  color={'primary'}
              >
                Load More
              </Button>
          </div>
      </div>
    );
  }
}

export default withRouter(connect(
    state => ({
        storeState: state
    }),
    dispatch => ({
        fetchPhones: () => {
            dispatch(fetchPhones());
        }
    })
)(Main));
