import React from 'react';
import Card from '@material-ui/core/Card';
import { FormControl, CardActions, CardContent, Button, Typography,
    Input, InputLabel, InputAdornment, MenuList, MenuItem, ListItemText } from '@material-ui/core';
import { Search,   } from '@material-ui/icons';
import {withRouter} from "react-router-dom";
import { connect } from "react-redux";
const R = require('ramda');

class MainMenu extends React.Component {
    render() {
        const count = this.props.shopBask.length;
        const sum = R.sum(this.props.shopBask);
        return(
            <div>
                <Card>
                    <CardActions>
                        <Button variant="contained" color={'primary'} fullWidth={true}>
                            {`${count} items - \$${sum}`}
                        </Button>
                    </CardActions>
                    <CardContent>
                        <Typography style={{ textAlign: "center", fontSize: "20px", fontFamily: "Times New Roman"}}>
                            Quick Shop
                        </Typography>
                    </CardContent>
                    <CardActions>
                        <FormControl fullWidth={true}>
                            <InputLabel>
                                Search phones
                            </InputLabel>
                            <Input
                                type={'text'}
                                endAdornment={
                                    <InputAdornment position="end">
                                        <Search/>
                                    </InputAdornment>
                                }
                            />
                        </FormControl>
                    </CardActions>
                    <CardContent>
                        <Typography style={{ fontSize: "20px", fontFamily: "Times New Roman"}}>
                            Brand
                        </Typography>
                        <MenuList>
                            <MenuItem>All</MenuItem>
                            <MenuItem>Apple</MenuItem>
                            <MenuItem>Samsung</MenuItem>
                            <MenuItem>Htc</MenuItem>
                            <MenuItem>Lenovo</MenuItem>
                            <MenuItem>Microsoft</MenuItem>
                        </MenuList>
                    </CardContent>
                </Card>
            </div>
        );
    }
}

export default withRouter(connect(
    state => ({
        shopBask: state.shopBask
    }),
    dispatch => ({})
)(MainMenu));